import { Component, OnInit } from '@angular/core';
import { GetDataService } from '../../../services/get-data.service';
import { ActivatedRoute } from '@angular/router';
import * as $ from 'jquery';


declare const require: any;
const jsPDF = require('jspdf');
require('jspdf-autotable');

@Component({
  selector: 'app-view-plan',
  templateUrl: './view-plan.component.html',
  styleUrls: ['./view-plan.component.css']
})
export class ViewPlanComponent implements OnInit {

  id: any;
  detail:Array<any> = [];

  circuit ={
    "nombrecircuital":'',
    "codigoCircuital":'',
    "lema":'',
    "nroplanteles":'',
    "estado":'',
    "parroquia":'',
    "municipio":'',
    "puntoycirculo":''
  };
   plan ={
    "titulo":'',
    "momento":''
  };
  image: HTMLImageElement;
  blobimg: any;
  constructor(public route: ActivatedRoute, private getData: GetDataService) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.id = params['id'];
      console.log(this.id);
    });
    this.getData.getFullPlan(this.id).subscribe(
      response =>{
       
        this.plan['titulo'] = response.plan.titulo;
        this.plan['momento'] = response.plan.momento;
        this.circuit['nombrecircuital'] = response.circuito.nombrecircuital;
        this.circuit['codigoCircuital'] = response.circuito.codigoCircuital;
        this.circuit['lema'] = response.circuito.lema;
        this.circuit['nroplanteles'] = response.circuito.nroplanteles;
        this.circuit['estado'] = response.circuito.estado;
        this.circuit['parroquia'] = response.circuito.parroquia;
        this.circuit['municipio'] = response.circuito.municipio;
        this.circuit['puntoycirculo'] = response.circuito.puntoycirculo;
        this.detail = response.detail;
        this.image = document.createElement('img');
       this.image.src = `
       data:image/png;base64,
       `;
       // console.log(this.plan);
        // console.log(this.circuit);
        console.log(this.detail);
      },
      error =>{
        console.log(error);
      }
    );
  }


  
printPDF(){


  let d = new Date();
  let n = d.toLocaleDateString();
  let doc = new jsPDF(); 
  let specialElementHandlers = {
    '#editor': function (element, renderer) {
        return true;
    }
  };
  doc.addImage(`${this.image.src}`, 'PNG', 55, 5, 100, 20);
  doc.setFont("helvetica");
  doc.setFontType("bold");
  doc.setFontSize(16);
  doc.text(`${this.plan['titulo']}`,75,40);
  doc.setFont("courier");
  doc.setFontSize(10);
  doc.text(`${n}`,95,45);
  doc.fromHTML($("#info").html(), 15, 50, {
    'width': 170,
        'elementHandlers': specialElementHandlers
  });
 
    doc.setFont("helvetica");
    doc.setFontType("bold");
    doc.setFontSize(16);
    doc.text('Plan de Acción', 80, 130);  
    doc.autoTable({
      startY: 140,
      html: '#datatable',
      });

  doc.save(`${this.plan['titulo']}.pdf`);
}

}

